import './styles.css'
import React, { useState } from 'react';


export default function Diary() {
  const [entries, setEntries] = useState([]); // used hook to store entries and set new ones
  const[currentEntry, setCurrentEntry] = useState(''); // stores our current entry

  const todaysDate = new Date().toLocaleString();

  const addEntry = () => { // function to add new entries
    if (currentEntry.trim() !== '') {
      setEntries([...entries, { text: currentEntry, date: new Date().toLocaleString() }]);
      setCurrentEntry('');
    }
  }
  return (
    <body className="bg-[conic-gradient(at_top_right,_var(--tw-gradient-stops))] from-gray-700 via-gray-900 to-black">
      <h1>
        DayLog
      </h1>
      <h2>
        {todaysDate}
      </h2>
      {entries.map((entry, index) => (
        <div key={index} className="diary-entry">
          <h3>{entry.date}</h3>
          <p>{entry.text}</p>
        </div>
      ))}
      <div className="entry-form">
        <div class="gradient-shadow"></div>
        <textarea 
          rows="17"
          placeholder="What's on your mind...?"
          value={currentEntry}
          onChange={(e) => setCurrentEntry(e.target.value)}
        />
        <button className = "entryButton" onClick={addEntry}>Add Entry</button>
        </div>
    </body>
  )
}
