import './styles.css'
import React, { useState, useEffect} from 'react';


export default function Diary() {
  const [entries, setEntries] = useState([]); // used hook to store entries and set new ones
  const[currentEntry, setCurrentEntry] = useState(''); // stores our current entry
  const [entryCount, setEntryCount] = useState(0); // sets our entry count
  const [todaysDate, setTodaysDate] = useState(new Date().toLocaleString());

  const addEntry = () => { // function to add new entries
    if (currentEntry.trim() !== '') {
      const newEntry = {
        text: currentEntry,
        date: new Date().toLocaleString(),
        entryNumber: entryCount + 1 // Assign a unique entry number
      };
      setEntries([...entries, newEntry]);
      setCurrentEntry('');
      setEntryCount(entryCount+1);
    }
  }

  // Use the useEffect hook to update the time every second
  useEffect(() => {
    const updateTime = () => {
      setTodaysDate(new Date().toLocaleString());
    };

    const intervalId = setInterval(updateTime, 1000); // Update every second

    return () => {
      clearInterval(intervalId); // Cleanup when the component unmounts
    };
  }, []);

  return (
    <body className="bg-[conic-gradient(at_top_right,_var(--tw-gradient-stops))] from-gray-700 via-gray-900 to-black">
      <h1>
        DayLog
      </h1>
      <h2>
        {todaysDate}
      </h2>
      <div className="gradient-shadow-behind"></div>
      <div className="entry-form">
      <div className ="entryBox">
        {entries.slice().reverse().map((entry, index) => (
          <div key={index} className="diary-entry">
            <h3>{entry.date}</h3>
            <p>Diary Entry #{entry.entryNumber}</p>
          </div>
        ))}
      </div>
        <div class="gradient-shadow"></div>
        <textarea 
          rows="17"
          placeholder="What's on your mind...?"
          value={currentEntry}
          onChange={(e) => setCurrentEntry(e.target.value)}
        />
        </div>
        <button className = "entryButton" onClick={addEntry}>Add Entry</button>
    </body>
  )
}
